# coding:utf-8import configparserimport loggingfrom Autotest_platform.helper.helperPath import pattern, BASE_DIRlog = logging.getLogger('log')# 配置文件路径configPath = BASE_DIR + pattern + 'Autotest_platform' + pattern + 'helper' + pattern + 'config' + pattern + 'cfg.ini'conf = configparser.ConfigParser()conf.read(configPath, encoding='utf-8')MySQL_host = conf.get('MySQL', 'MySQL_host')MySQL_user = conf.get('MySQL', 'MySQL_user')MySQL_pwd = conf.get('MySQL', 'MySQL_pwd')MySQL_database = conf.get('MySQL', 'MySQL_database')MySQL_port = conf.get('MySQL', 'MySQL_port')MySQL_host_job = conf.get('MySQL', 'MySQL_host_job')MySQL_user_job = conf.get('MySQL', 'MySQL_user_job')MySQL_pwd_job = conf.get('MySQL', 'MySQL_pwd_job')MySQL_database_job = conf.get('MySQL', 'MySQL_database_job')MySQL_port_job = conf.get('MySQL', 'MySQL_port_job')email_user = conf.get('email', 'email_user')email_pwd = conf.get('email', 'email_pwd')if __name__ == '__main__':    print(configPath)